INSERT INTO `posts` (`id`, `title`, `summary`, `body`, `view`, `user_id`, `cat_id`, `image`, `status`, `selected`, `breaking_news`, `published_at`, `created_at`, `updated_at`, `author_name`, `file`) VALUES
(153, 'Tạp chí NCKH số 11', 'Ở điều kiện thường, một số ion kim loại có khả năng tạo phức với một số ligand hữu cơ', 'Ở điều kiện thường, một số ion kim loại có khả năng tạo phức với một số ligand hữu cơ. \r\nMỗi phức chất được đặc trưng bởi hằng số bền (Kb). Những phức có Kb >109\r\nkhông só \r\nhoạt tính xúc tác, nhưng chúng lại có khả năng kháng khuẩn và kháng nấm cao. Bên \r\ncạnh đó, những phức có hằng số bền từ 103\r\n< Kb < 109\r\ncó hoạt tính xúc tác, chúng có vai \r\ntrò quan trọng trong một số quá trình như: Phân tích vi lượng, xử lý nước, chất thải công \r\nnghiệp. Trong giới hạn bài báo này, phản ánh kết quả nghiên cứu khả năng kháng khuẩn \r\nE.coli ATCC 25922 và kháng chủng nấm mốc Trichoderma receive CPK 63 của một số\r\nphức chất, tạo bởi ion kim loại Ni2+; Co2+; Zn2+ và Cu2+ với glyxin. Kết quả nghiên cứu là \r\ncơ sở khoa học cho việc ứng dụng các phức chất vào các quá trình công nghệ sản xuất.', 0, 1, 15, '', 'disable', 1, 1, '2023-07-20 00:00:00', '2023-07-20 17:18:14', NULL, 'Lê Văn Huỳnh, Nguyễn Thị Hiền, Phan Thị Thanh Hương, Phạm Thị Thu', './public/upload/2023-07-20-12-18-149. Huỳnh.pdf'),
(154, 'Tạp chí NCKH số 12', 'Phương trình toán tử ngẫu nhiên có nhiễu là một trong các hướng nghiên cứu của lý\r\nthuyết xác suất hiện đại. ', 'Phương trình toán tử ngẫu nhiên có nhiễu là một trong các hướng nghiên cứu của lý\r\nthuyết xác suất hiện đại. Bài báo nghiên cứu tính duy nhất nghiệm của phương trình \r\ntoán tử ngẫu nhiên có nhiễu, áp dụng lý thuyết điểm bất động để tìm điều kiện đủ của bài \r\ntoán trên. Kết quả nghiên cứu là cơ sở để tìm lời giải phương trình ngẫu nhiên có nhiễu.', 0, 1, 15, '', 'disable', 1, 1, '2012-05-01 00:00:00', '2023-07-20 17:20:11', NULL, 'Trần Thị Kim Thanh', './public/upload/2023-07-20-12-20-119. Thanh.pdf'),
(156, 'Tạp chí NCKH số 13', 'Hai giống chè Phúc Vân Tiên  và  Kim Tiên được thu hoạch 1 búp 3 lá vào buổi sáng tại Viện Khoa học Nông nghiệp miền núi phía Bắc vào thời điểm tháng 7,8/2016. ', 'Hai giống chè Phúc Vân Tiên  và  Kim Tiên được thu hoạch 1 búp 3 lá vào buổi sáng tại Viện Khoa học Nông nghiệp miền núi phía Bắc vào thời điểm tháng 7,8/2016. Sau đó, chè được dải đều trên nong, để héo tự nhiên trong khoảng thời gian 240 phút. Để bất hoạt enzyme trong chè sử dụng diệt men phương pháp vi sóng ở điều kiện độ dầy lớp chè diệt men trong khoảng 120 mm tương đương với mật độ đọt chè là 1,5 kg/m2 đối với chè PVT và 1,2kg/m2 đối với chè KT; Nhiệt độ diệt men 83-85oC và thời gian diệt men 205 - 227 giây đối với chè Phúc Vân Tiên; 224 - 272 giây đối với chè Kim Tuyên. Với chế độ diệt men như vậy trong chè hoạt tính men peroxyaza hoàn toàn không còn nữa. Điều này còn khẳng định tất cả enzyme trong chè hoàn toàn bị bất hoạt. Chất lượng sản phẩm matcha khi diệt men bằng phương pháp vi sóng có chất lượng tốt hơn so với phương pháp truyền thống.', 0, 1, 15, '', 'disable', 1, 1, '2015-05-20 00:00:00', '2023-07-20 17:24:15', NULL, 'Đặng Thị Thanh Quyên', './public/upload/2023-07-20-12-24-157.13. ĐTT Quyên.doc'),
(157, 'Tạp chí NCKH số 14', 'Phương pháp mô phỏng Monte-Carlo được sử dụng để nghiên cứu sự ảnh hưởng của \r\nkích thước hạt nano tinh thể được tạo ra một cách ngẫu nhiên trên bề mặt vật liệu và \r\nđược xem như là các trung tâm tái tổ hợp.', 'Phương pháp mô phỏng Monte-Carlo được sử dụng để nghiên cứu sự ảnh hưởng của \r\nkích thước hạt nano tinh thể được tạo ra một cách ngẫu nhiên trên bề mặt vật liệu và \r\nđược xem như là các trung tâm tái tổ hợp. Dòng điện cảm ứng chùm điện tử thu nhận \r\nbởi nano contact. Kết quả mô phỏng cho thấy, dòng điện cảm ứng chùm điện tử giảm khi \r\nkích thước hạt nano tinh thể tăng, quan hệ của chúng theo một hàm mũ bậc một. Độ dài \r\nkhuếch tán hiệu dụng của hạt tối thiểu cũng được rút ra từ đường cong dòng điện cảm \r\nứng chùm điện tử. Ảnh hưởng của kích thước hạt nano tinh thể nên độ dài khuếch tán \r\ncũng được xem xét, kết quả chỉ ra rằng độ dài khuếch tán giảm khi kích thước hạt nano \r\ntăng. Điều này được giải thích do sự tăng vùng tái tổ hợp bề mặt', 0, 1, 15, '', 'disable', 1, 1, '2008-04-20 00:00:00', '2023-07-20 17:27:27', NULL, 'Đoàn Quảng Trị, Vũ Ngọc Phan, Dương Xuân Núi , Dương Chính Cương, Nguyễn Văn Đạt\r\n', './public/upload/2023-07-20-12-27-279. Trị.pdf'),
(158, 'Tạp chí NCKH số 15', 'Nghiên cứu chế biến bột khoai uống liền từ khoai Lang Hoàng Long, giống vỏ nâu, ruột \r\nvàng, thu hoạch tháng 7, năm 2016.', 'Nghiên cứu chế biến bột khoai uống liền từ khoai Lang Hoàng Long, giống vỏ nâu, ruột \r\nvàng, thu hoạch tháng 7, năm 2016. Kết quả nghiên cứu cho thấy, axit axetic có tác dụng \r\ntốt trong việc hạn chế phản ứng sẫm màu khi chế biến khoai lang củ tươi. Ở nồng độ\r\n0,3% và thời gian ngâm 30 phút xử lý khoai lát, sản phẩm bột khoai đạt độ trắng tốt: \r\n78%. Khoai lát sau xử lý được hấp chín và sấy ở nhiệt độ 70°C cho chất lượng bột khoai \r\ntốt nhất về hương vị và màu sắc. Bột khoai phối trộn thêm 5÷ 6% bột đậu xanh và 4÷5% \r\nbột nếp cùng với đường kính 10%, muối ăn 0,1%, đạt được tính chất cảm quan tốt làm \r\ntăng giá trị dinh dưỡng cho bột khoai uống liền.', 0, 1, 15, '', 'disable', 1, 1, '2017-04-20 00:00:00', '2023-07-20 17:29:31', NULL, 'Vũ Thị Ngọc Bích', './public/upload/2023-07-20-12-29-315 Bich.pdf'),
(159, 'Tạp chí NCKH số 16', 'Việc xác định chế độ cắt tối ưu cho quá trình mài nhằm nâng cao chất lượng chi tiết và \r\nhạ giá thành sản phẩm rất quan trọng trong quá trình gia công. ', 'Việc xác định chế độ cắt tối ưu cho quá trình mài nhằm nâng cao chất lượng chi tiết và \r\nhạ giá thành sản phẩm rất quan trọng trong quá trình gia công. Thép X12M thuộc nhóm \r\nthép hợp kim cao, có độ bền nhiệt và độ bền cơ học cao. Vật liệu này thường dùng để\r\nchế tạo các chi tiết máy chính xác, chịu tải va đập lớn, chịu mài mòn và ăn mòn hóa học.\r\nTrong bài báo này, trên cơ sở ứng dụng phần mềm toán học Mable, tác giả đã kết hợp \r\ngiữa phân tích lý thuyết với thực nghiệm để tìm ra bộ thông số chế độ cắt tối ưu khi gia \r\ncông thép X12M trên máy mài tròn ngoài', 0, 1, 15, '', 'disable', 1, 1, '2017-12-02 00:00:00', '2023-07-20 17:31:11', NULL, 'Nguyễn Văn Trúc, Bùi Ánh Hưng, Trịnh Thị Mai', './public/upload/2023-07-20-12-31-11Tạp chí số 16.pdf'),
(160, 'Tạp chí NCKH số 17', 'Rung động là trạng thái giới hạn của tự dao động trong quá trình gia công, là nguyên nhân chính hạn chế năng suất và chất lượng chi tiết gia công.', 'Rung động là trạng thái giới hạn của tự dao động trong quá trình gia công, là nguyên nhân chính hạn chế năng suất và chất lượng chi tiết gia công. Vì vậy các công trình nghiên cứu hiện nay đều tập trung vào phát hiện, nhận dạng, tránh, ngăn ngừa, giảm hoặc loại trừ rung động. Có nhiều phương pháp phát hiện và đo rung động đã được đề xuất, mỗi phương pháp có ưu, nhược điểm riêng. Bài báo này trình bày phương pháp dựa trên quan hệ giữa lực mài và các thông số rung động. Kết quả nghiên cứu đã chỉ ra rằng tín hiệu lực cắt không chỉ giúp phát hiện rung động mà còn dự báo nó trước khi xảy ra.', 0, 1, 15, '', 'disable', 1, 1, '2016-05-11 00:00:00', '2023-07-20 17:33:39', NULL, 'Phạm Vũ Dũng', './public/upload/2023-07-20-12-33-39File 17 MI.doc'),
(161, 'Tạp chí NCKH số 19', 'Bài báo trình bày kết quả nghiên cứu các yếu tố ảnh hưởng đến quá trình xử lý nước thải phát sinh trong quá trình sản xuất sôđa (Na2CO3) là vấn đề được quan tâm để từ đó tính toán các thông số công nghệ tối ưu, đề xuất dây chuyền công nghệ phù hợp với điều kiện tại Việt Nam.', 'Bài báo trình bày kết quả nghiên cứu các yếu tố ảnh hưởng đến quá trình xử lý nước thải phát sinh trong quá trình sản xuất sôđa (Na2CO3) là vấn đề được quan tâm để từ đó tính toán các thông số công nghệ tối ưu, đề xuất dây chuyền công nghệ phù hợp với điều kiện tại Việt Nam. Tác giả đã nghiên cứu thực nghiệm, sử dụng phần mềm xử lý số liệu thực nghiệm SPSS 23.0 và phương pháp phân tích ANOVA để xác định sự ảnh hưởng của thông số thời gian và tốc độ khuấy đến hiệu suất xử lý nước thải phát sinh trong quá trình sản xuất sôđa.', 0, 1, 15, '', 'disable', 1, 1, '2003-11-01 00:00:00', '2023-07-20 17:37:35', NULL, 'Phạm Thị Thu Hoài, Nguyễn Thị Phương Lan, Phạm Thị Thanh Thuỷ', './public/upload/2023-07-20-12-37-351. Hoài.doc'),
(162, 'Tạp chí NCKH số 20', 'Bài báo đã trình bày và ứng dụng mô hình ra quyết định nhóm dạng khoảng để biểu diễn bài toán đánh giá năng lực giảng viên.', 'Bài báo đã trình bày và ứng dụng mô hình ra quyết định nhóm dạng khoảng để biểu diễn bài toán đánh giá năng lực giảng viên. Phương pháp giải quyết được đề xuất dựa trên số học khoảng và hàm so sánh toàn phần trên tập các số thực dạng khoảng. Dữ liệu và kết quả thử nghiệm được xây dựng với bộ 25 tiêu chí dùng để đánh giá năng lực giảng viên Trường Đại học Kinh tế - Kỹ thuật Công nghiệp. ', 0, 1, 15, '', 'disable', 1, 1, '2019-11-11 00:00:00', '2023-07-20 17:39:08', NULL, 'Vũ Thị Thu Huyền, Trần Thị Hương', './public/upload/2023-07-20-12-39-085. Huyền.doc'),
(163, 'Tạp chí NCKH số 22', 'Ngày nay, trong sản xuất hiện đại không thể thiếu các hệ thống cấp phôi tự động. Điểm \r\nmạnh của thiết bị cấp phôi là việc điều hướng, phối liệu đúng nhịp, đơn giản và chính xác \r\nhơn hẳn các hệ thống truyền dẫn khác. ', 'Ngày nay, trong sản xuất hiện đại không thể thiếu các hệ thống cấp phôi tự động. Điểm \r\nmạnh của thiết bị cấp phôi là việc điều hướng, phối liệu đúng nhịp, đơn giản và chính xác \r\nhơn hẳn các hệ thống truyền dẫn khác. Đối với loại phôi rời có trọng lượng và kích thước \r\nnhỏ thì phương pháp các phôi tự động theo nguyên lý rung động là một lựa chọn phù hợp. \r\nBài báo này trình bày các lý thuyết cơ bản khi tính toán thiết kế một hệ thống cấp phôi tự\r\nđộng cho phôi có trọng lượng và kích thước nhỏ. Lý thuyết này được áp dụng để tính toán, \r\nthiết kế thiết bị cấp phôi cho nắp chai vacxin bằng cao su. Kết quả của nghiên cứu là cơ sở\r\ncho các tính toán thiết kế và chế tạo một thiết bị cấp phôi rung cho các loại phôi khác nhau.', 0, 1, 15, '', 'disable', 1, 1, '1970-01-01 01:00:00', '2023-07-20 17:46:07', '2023-07-20 17:47:57', 'Nguyễn Văn Mùi - Vũ Hoài Anh', './public/upload/2023-07-20-12-46-076. Mùi (da sua).pdf'),
(164, 'Tạp chí NCKH số 23', 'Các yếu tố được nghiên cứu bao gồm nguồn dinh dưỡng cacbon, dinh dưỡng nitơ, nhiệt độ và thời gian nuôi cấy để xác định điều kiện thích hợp cho khả năng sinh bào tử cao nhất của hai chủng nấm Lecanicillum lecanii Le85 và L.lecanii L439 khi lên men lỏng quy mô 100 L/mẻ.', 'Các yếu tố được nghiên cứu bao gồm nguồn dinh dưỡng cacbon, dinh dưỡng nitơ, nhiệt độ và thời gian nuôi cấy để xác định điều kiện thích hợp cho khả năng sinh bào tử cao nhất của hai chủng nấm Lecanicillum lecanii Le85 và L.lecanii L439 khi lên men lỏng quy mô 100 L/mẻ. Môi trường lên men chứa 4% thành phần rỉ đường kích thích khả năng sinh bào tử của cả hai chủng Le85 và L439, với mật độ lần lượt đạt (5,02 ± 0,26)×109 và (6,75 ± 0,26)×109 bào tử/mL. Urea được lựa chọn là nguồn nitrogen phù hợp cho việc sản xuất bào tử của cả hai chủng. Nồng độ ure (0,6%) phù hợp đối với chủng Le85 và 0,3% đối với chủng L439 với mật độ bào tử trong môi trường lên men lỏng đạt (5,37 ± 0,15)×109 và (7,19 ± 0,023)×109 bào tử/mL. Sau thời gian lên men lỏng 8 ngày và tại nhiệt độ 28˚C các chủng Le85 và L439 đạt mật độ (7,88 ± 0,026)×109 và (8,92 ± 0,02)×109 bào tử/mL. Nghiên cứu đã xác định được điều kiện lên men lỏng cho việc sinh bào tử của các chủng nấm kí sinh côn trùng L.lecanii Le85 và L439 có vai trò quan trọng trong cải thiện hiệu quả sản xuất chế phẩm thuốc trừ sâu sinh học phòng trừ rệp muội Aphidoidae.', 0, 1, 16, '', 'disable', 1, 1, '2020-08-06 00:00:00', '2023-07-20 17:52:21', NULL, 'Vũ Văn Hạnh, Hồ Tuấn Anh, Đỗ Thị Lý, Nguyễn Thị Nguyệt, Nguyễn Quỳnh Mai', './public/upload/2023-07-20-12-52-214. HT Anh.doc'),
(165, 'Tạp chí NCKH số 24', 'Độ tròn là một chỉ tiêu kỹ thuật để đánh giá sai số hình dáng hình học của các chi tiết cơ khí dùng trong các lắp ghép có độ chính xác cao như ổ lăn, piston xilanh, bề mặt trụ trơn lắp với ổ lăn, ổ trượt, sống dẫn hướng trụ', 'Độ tròn là một chỉ tiêu kỹ thuật để đánh giá sai số hình dáng hình học của các chi tiết cơ khí dùng trong các lắp ghép có độ chính xác cao như ổ lăn, piston xilanh, bề mặt trụ trơn lắp với ổ lăn, ổ trượt, sống dẫn hướng trụ,... Do đó việc đánh giá sai lệch độ tròn là nhiệm vụ thiết yếu, có ý nghĩa quan trọng trong việc chỉ đạo công nghệ gia công nhằm đảm bảo chất lượng chi tiết, mang lại giá trị kinh tế. Bài báo này trình bày tổng quan về một số giải pháp nhằm nâng cao độ chính xác của phép đo độ tròn trong tọa độ cực bằng cách sử dụng ổ khí quay, ứng dụng khai triển Fourier kết hợp với ba đầu đo để đảm bảo độ ổn định tâm quay, khử bỏ độ dao động tâm bàn quay và độ lệch giữa tâm chi tiết và tâm bàn quay.', 0, 1, 15, '', 'disable', 1, 1, '2021-01-09 00:00:00', '2023-07-20 17:54:51', NULL, 'Nguyễn Hữu Quang, Nguyễn Anh Tuấn, Phan Trọng Đức  ', './public/upload/2023-07-20-12-54-514. NH Quang MI.doc'),
(166, 'Tạp chí NCKH số 25', 'Bài báo này nghiên cứu mối quan hệ và đánh giá ảnh hưởng của các thông số công nghệ \r\nđến độ nhám bề mặt chi tiết khi phay hợp kim nhôm AA7075; xây dựng phương trình hồi quy \r\nđộ nhám bề mặt khi gia công không sử dụng dung dịch trơn nguội; nghiên cứu thực nghiệm \r\nso sánh độ nhám bề mặt khi gia công có sử dụng dung dịch trơn nguội v', 'Bài báo này nghiên cứu mối quan hệ và đánh giá ảnh hưởng của các thông số công nghệ \r\nđến độ nhám bề mặt chi tiết khi phay hợp kim nhôm AA7075; xây dựng phương trình hồi quy \r\nđộ nhám bề mặt khi gia công không sử dụng dung dịch trơn nguội; nghiên cứu thực nghiệm \r\nso sánh độ nhám bề mặt khi gia công có sử dụng dung dịch trơn nguội và không sử dụng \r\ndung dịch trơn nguội và đưa ra các biểu đồ mức độ ảnh hưởng của các thông số công nghệ \r\nđến độ nhám bề mặt; đánh giá hiệu quả của việc gia công có sử dụng dung dịch làm mát và \r\nkhông sử dụng dung dịch làm mát. Kết quả nghiên cứu làm cơ sở đề các nhà công nghệ lựa \r\nchọn điều kiện gia công cho phù hợp kinh tế mà vấn đảm bảo được năng suất và chất lượng \r\nchi tiết gia công.\r\n', 0, 1, 17, '', 'disable', 1, 1, '2023-07-20 00:00:00', '2023-07-20 17:56:59', NULL, 'Lê Như Trang, Trần Xuân Thái, Nguyễn Trọng Hải\r\n', './public/upload/2023-07-20-12-56-596. Trang MI.pdf'),
(167, 'Tạp chí NCKH số 26', 'Bài báo trình bày thuật điều khiển thông minh các khóa chuyển mạch cầu nghịch lưu ba pha \r\nở luật chuyển mạch 180o\r\nđể điều khiển động cơ không đồng bộ ba pha nhằm loại bỏ dòng \r\nđiện trùng dẫn.', 'Bài báo trình bày thuật điều khiển thông minh các khóa chuyển mạch cầu nghịch lưu ba pha \r\nở luật chuyển mạch 180o\r\nđể điều khiển động cơ không đồng bộ ba pha nhằm loại bỏ dòng \r\nđiện trùng dẫn. Các kết quả được khảo sát đánh giá bằng mô hình mô phỏng trên phần mềm \r\nMatlab-Simulink. Mô hình cho phép đánh giá so sánh chất lượng điện áp được điều chế giữa \r\nphương pháp cải tiến và cổ điển, đồng thời chỉ ra rằng với luật chuyển mạch cầu nghịch lưu \r\nba pha cải tiến đã loại bỏ được dòng điện trùng dẫn trong các nhánh của cầu nghịch lưu so \r\nvới luật chuyển mạch cổ điển', 0, 1, 15, '', 'disable', 1, 1, '1970-01-01 01:00:00', '2023-07-20 17:58:32', '2023-07-20 17:58:42', 'Lê Văn Ánh', './public/upload/2023-07-20-12-58-322 Lê Văn Ánh.pdf'),
(168, 'Tạp chí NCKH số 27', 'Xử lý ngôn ngữ tự nhiên là một lĩnh vực nghiên cứu đa dạng với nhiều nhiệm vụ riêng biệt, \r\nmỗi nhiệm vụ được đánh giá bởi bộ dữ liệu đặc thù. ', 'Xử lý ngôn ngữ tự nhiên là một lĩnh vực nghiên cứu đa dạng với nhiều nhiệm vụ riêng biệt, \r\nmỗi nhiệm vụ được đánh giá bởi bộ dữ liệu đặc thù. Trong thực tế, một số bài toán có ít dữ \r\nliệu gán nhãn chất lượng cao để huấn luyện mô hình, dẫn tới hiệu năng chưa cao. Giải \r\nquyết vấn đề này, một số mô hình sử dụng cơ chế tiền xử lý dữ liệu huấn luyện bằng việc sử \r\ndụng các mô hình biểu diễn từ chung, được huấn luyện từ một lượng lớn các dữ liệu không \r\nđược gán nhãn như Word2vec, Glove hay FastText. Tuy nhiên, các mô hình đó không thể \r\nhiện được sự đại diện theo ngữ cảnh cụ thể của từ. Trong dịch máy thường sử dụng kiến \r\ntrúc Recurrent Neural Networks. Mô hình này khó bắt được sự phụ thuộc xa giữa các từ \r\ntrong câu và tốc độ huấn luyện chậm. Transformers là một kiến trúc mạng nơron nhân tạo \r\nđược đề xuất để khắc phục các nhược điểm trên. Bài báo này, chúng tôi trình bày kiến trúc \r\nTransformers, đề xuất mô hình dịch máy sử dụng kiến trúc Transformers. Kết quả thực \r\nnghiệm trên cặp ngôn ngữ Anh - Việt và Việt - Anh chứng minh rằng, mô hình do chúng tôi \r\nđề xuất đạt hiệu năng vượt trội so với các mô hình trước đó.', 0, 1, 15, '', 'disable', 1, 1, '2020-08-12 00:00:00', '2023-07-20 18:00:25', NULL, 'Trần Hồng Việt, Nguyễn Thu Hiền ', './public/upload/2023-07-20-13-00-255. Trần Hồng Việt.pdf'),
(169, 'Tạp chí NCKH số 28', 'Thép C40 là vật liệu khó gia công nhưng được ứng dụng nhiều trong thực tiễn. ', 'Thép C40 là vật liệu khó gia công nhưng được ứng dụng nhiều trong thực tiễn. Trong bài \r\nbáo, tác giả đã trình bày mô hình thực nghiệm gia công thép C40 trên máy tiện CNC, với sự \r\nhỗ trợ của phần mềm toán học Maple đã tìm ra mối quan hệ giữa thông số chế độ cắt với độ \r\nnhám bề mặt của chi tiết gia công. Từ đó, xác định được chế độ cắt theo tiêu chí tối ưu độ \r\nnhám bề mặt khi gia công thép C40 trên máy tiện ROTURN 400 C', 0, 1, 15, '', 'disable', 1, 1, '2021-05-17 00:00:00', '2023-07-20 18:01:46', NULL, 'Nguyễn Văn Trúc, Bùi Ánh Hưng, Trần Trọng Thể', './public/upload/2023-07-20-13-01-464. 5.Trúc MI.pdf'),
(170, 'Tạp chí NCKH số 29', 'Mục tiêu của nghiên cứu này là đánh giá ảnh hưởng của tỷ lệ maltodextrin bổ sung đến chất \r\nlượng của bột bơ. ', 'Mục tiêu của nghiên cứu này là đánh giá ảnh hưởng của tỷ lệ maltodextrin bổ sung đến chất \r\nlượng của bột bơ. Chất lượng của bột bơ được đánh giá dựa vào chỉ tiêu cảm quan và các \r\nchỉ liêu lý - hóa bao gồm khả năng giữ nước, độ ẩm, hàm lượng lipid, chỉ số acid và chỉ số \r\nperoxide và hàm lượng vitamin C. Kết quả thu được cho thấy tỷ lệ maltodextrin bổ sung phù \r\nhợp là 13%. Các tính chất lý - hóa của bột bơ thu được là: hàm lượng lipid tổng đạt 15,6%, \r\nđộ ẩm của bột bơ < 5%; chỉ số hư hỏng của dầu thấp: chỉ số peroxide đạt 16,18 meq/kg, chỉ \r\nsố acid đạt 2,69 mg/g; khả năng giữ nước của của bột cao', 0, 1, 15, '', 'disable', 1, 1, '2021-03-25 00:00:00', '2023-07-20 18:03:21', NULL, 'Nguyễn Thị Thảo, Nguyễn Văn Minh', './public/upload/2023-07-20-13-03-215. Thảo đã sửa.pdf'),
(171, 'Tạp chí NCKH số 30', 'Trong bài báo này, chúng tôi trình bày về việc biến tính cảm biến trên cơ sở màng SnO2 với \r\ncác hạt nano bạc hoặc platin nhằm tăng cường khả năng nhạy khí với khí hydro sulfua \r\n(H2S). ', 'Trong bài báo này, chúng tôi trình bày về việc biến tính cảm biến trên cơ sở màng SnO2 với \r\ncác hạt nano bạc hoặc platin nhằm tăng cường khả năng nhạy khí với khí hydro sulfua \r\n(H2S). Các cảm biến chế tạo được được khảo sát tính chất nhạy khí, cấu trúc hình thái học. \r\nKết quả nghiên cứu cho thấy sự có mặt của các hạt nano Ag hoặc Pt đều cải thiện đáng kể \r\nkhả năng nhạy khí của cảm biến với H2S. Trong đó các mẫu cảm biến biến tính với lớp hạt \r\nnano Ag (dày 1 mm) hay Pt (dày 4 mm) có khả năng nhạy khí tốt nhất ở 250 oC với nồng độ \r\nkhí H2S là 0,25 ppm', 0, 1, 15, '', 'disable', 1, 1, '2021-11-15 00:00:00', '2023-07-20 18:05:20', NULL, 'Vo Thanh Duoc, Duong Thi Thuy Trang, Nguyen Van Toan, Lam Thi Hang, Bui Tien Trinh, Nguyen Si Hai, Vu Ngoc Phan Phung Thi Hong Van, Nguyen Van Duy', './public/upload/2023-07-20-13-05-203 (6). Van Mi.pdf'),
(172, 'Tạp chí NCKH số 31', 'Bài báo so sánh kết quả mô phỏng và thực nghiệm áp suất màng dầu bôi trơn trong ổ đầu to \r\nđỡ thủy động.', 'Bài báo so sánh kết quả mô phỏng và thực nghiệm áp suất màng dầu bôi trơn trong ổ đầu to \r\nđỡ thủy động. Mô phỏng bôi trơn áp suất thủy động cho ổ đầu to thanh truyền trên cơ sở giải \r\ncác phương trình Reynolds, phương trình chiều dày màng dầu, phương trình cân bằng tải \r\nkết hợp mô phỏng số bôi trơn cho ổ bằng phương pháp mô hình hoá bài toán áp suất cho \r\nmàng dầu áp dụng phần tử hữu hạn. Thiết bị thực nghiệm với ổ đỡ thủy động chịu tải tác \r\ndụng trực tiếp lên ổ. Áp suất được đo bằng năm cảm biến đặt tại năm vị trí phân bố đều ở \r\nvùng áp suất dương và tại tiết diện giữa ổ theo phương chiều dài ổ. Kết quả nghiên cứu chỉ \r\nra rằng áp suất màng dầu bôi trơn trong ổ đỡ thủy động tăng lên khi tải tác dụng lên ổ tăng. \r\nKhi tải tác dụng lên ổ tăng lên thì áp suất màng dầu tại vị trí chiều dày màng dầu nhỏ nhất \r\nđạt giá trị lớn nhất và khi tăng tốc độ quay thì áp suất màng dầu tại vị trí lớn nhất giảm đi.\r\n', 0, 1, 15, '', 'disable', 1, 1, '2021-05-14 00:00:00', '2023-07-20 18:06:41', NULL, 'Trịnh Thị Mai, Phạm Trung Thiên, Nguyễn Ngọc Thể, Đỗ Văn Tỉnh', './public/upload/2023-07-20-13-06-414.Thiên MII.pdf'),
(173, 'Tạp chí NCKH số 32', 'Vật liệu có từ tính ngày càng được nghiên cứu rộng rãi, bởi những ứng dụng của nó vào \r\nthực tiễn rất có hiệu quả. TiO2 khi được gắn nên chất mang NiFe2O4 là chất xúc tác quang \r\nhoá, chúng có khả năng phân hủy nhiều hợp chất hữu cơ mang màu ra khỏi môi trường \r\nnước như metyl da cam. ', 'Vật liệu có từ tính ngày càng được nghiên cứu rộng rãi, bởi những ứng dụng của nó vào \r\nthực tiễn rất có hiệu quả. TiO2 khi được gắn nên chất mang NiFe2O4 là chất xúc tác quang \r\nhoá, chúng có khả năng phân hủy nhiều hợp chất hữu cơ mang màu ra khỏi môi trường \r\nnước như metyl da cam. Khi xử lý nước thải chứa các hợp chất hữu cơ mang màu, thì TiO2\r\nở dạng huyền phù trong môi trường nước, thường không thể thu hồi lại được TiO2 để tái sử \r\ndụng, gây lãng phí, gây ô nhiễm môi trường, tạo ra chất thải rắn. Hạt tinh thể nano NiFe2O4\r\nđược tổng hợp bằng phương pháp thuỷ nhiệt và được sử dụng làm chất mang xúc tác \r\nquang hóa TiO2. Bài báo này là kết quả nghiên cứu ứng dụng phân huỷ hợp chất hữu cơ \r\nmetyl da cam có nồng độ 2M bằng chất xúc tác quang hoá TiO2, sau 12 giờ xử lý đạt hiệu \r\nsuất 98,7%. Thu hồi được TiO2 để tái sử dụng lại nhiều lần, mà không tạo ra chất thải rắn \r\nTiO2 trong quá trình xử lý các hợp chất hữu cơ mang màu trong môi trường nước. Kết quả \r\nnghiên cứu là cơ sở khoa học cho việc áp dụng vào thực tiễn các quá trình công nghệ \r\nsản xuất', 0, 1, 15, '', 'disable', 1, 1, '2021-06-21 00:00:00', '2023-07-20 18:07:58', NULL, 'Trần Thị Hà', './public/upload/2023-07-20-13-07-584. Hà.pdf'),
(174, 'Tạp chí NCKH số 33', 'Thế giới bùng nổ cuộc Cách mạng công nghiệp 4.0 với các ứng dụng robot và trí tuệ nhân \r\ntạo đi sâu vào cuộc sống cũng như sinh hoạt hàng ngày. Các robot cũng như thiết bị tự động \r\nđang trở nên thông minh hơn theo cách của chúng để tương tác với cả con người cũng như \r\ngiao tiếp giữa các thiết bị với nhau.', 'Thế giới bùng nổ cuộc Cách mạng công nghiệp 4.0 với các ứng dụng robot và trí tuệ nhân \r\ntạo đi sâu vào cuộc sống cũng như sinh hoạt hàng ngày. Các robot cũng như thiết bị tự động \r\nđang trở nên thông minh hơn theo cách của chúng để tương tác với cả con người cũng như \r\ngiao tiếp giữa các thiết bị với nhau. Lĩnh vực xử lý ngôn ngữ tự nhiên trong trí tuệ nhân tạo \r\nlà công nghệ nhận biết ngôn ngữ tự nhiên của con người ngày càng phát triển và được ứng \r\ndụng rộng rãi. Những nghiên cứu này sẽ hỗ trợ tương tác tự nhiên giữa người và máy, trong \r\nđó máy sẽ học cách hiểu ngôn ngữ của con người, điều chỉnh và tương tác chủ động. Bài \r\nbáo này nghiên cứu trợ lý ảo có thể chủ động giao tiếp, tương tác với người sử dụng bằng \r\ncông nghệ trí thông minh nhân tạo. Mô hình huấn luyện nhận diện giọng nói và phân tích \r\ngiọng nói phát triển trên thư viện Speech recognition, phân tích âm thanh qua thư viện \r\nPyaudio và playsound, nguồn dữ liệu tìm kiếm truy vấn trên cơ sở dữ liệu trực tuyến. Công \r\nnghệ xây dựng trợ lý ảo hoàn toàn sử dụng thư viện mã nguồn mở, không bị hạn chế bởi \r\nđám mây lưu chữ thông tin và dữ liệu huấn luyện đầu vào. Trợ lý ảo có thể ứng dụng rộng \r\nrãi trong giao tiếp, giáo dục, và các ngành dịch vụ đạt độ chính xác hơn 90% và nhóm đang \r\ntiếp tục cải thiện tối ưu hóa hệ thống.', 0, 1, 15, '', 'disable', 1, 1, '1970-01-01 01:00:00', '2023-07-20 18:09:31', '2023-07-20 18:14:07', 'Nguyễn Tiến Dũng, Phạm Trung Thiên*, Lê Ngọc Dũng, Đỗ Văn Tỉnh, Vũ Xuân Tú, Nguyễn Văn Hiệp, \r\nNguyễn Ngọc Thể', './public/upload/2023-07-20-13-09-315.(8) Thiên MI.pdf'),
(176, 'Tạp chí NCKH số 34', 'Vật liệu điện cực cacbon xốp dẫn điện trên cơ sở cacbon hoạt tính gáo dừa đã được chế tạo \r\nlàm điện cực xử lý nước lợ theo công nghệ điện dung khử ion (Capacitive Deionization -\r\nCDI).', 'Vật liệu điện cực cacbon xốp dẫn điện trên cơ sở cacbon hoạt tính gáo dừa đã được chế tạo \r\nlàm điện cực xử lý nước lợ theo công nghệ điện dung khử ion (Capacitive Deionization -\r\nCDI). Ảnh hưởng của thông số chiều dày màng compozit và điện thế áp đặt đến tính chất \r\nđặc trưng cũng như khả năng hấp phụ muối của điện cực đã được nghiên cứu. Kết quả của \r\nnghiên cứu này chỉ ra rằng chiều dày màng là một thông số quan trọng ảnh hưởng đến dung \r\nlượng hấp phụ muối của điện cực. Màng compozit chế tạo càng dày thì điện trở khối của \r\nđiện cực càng lớn dẫn đến giảm dung lượng hấp phụ muối. Nghiên cứu ảnh hưởng của \r\nđiện thế áp đặt đến điện dung hấp phụ của vật liệu cho thấy giá trị độ hấp phụ muối cực đại \r\nSAC đạt cao nhất tại điện thế 1,2 V (10,93 mg/g và 11,97 mg/g cho chế độ hoạt động tuần \r\nhoàn và chế độ hoạt động liên tục).\r\n', 0, 1, 15, '', 'disable', 1, 1, '2022-09-15 00:00:00', '2023-07-20 18:13:57', NULL, 'Nguyễn Thị Thơm, Võ Thị Kiều Anh, Nguyễn Thị Thu Trang, Nguyễn Thu Phương, Đinh Thị Mai Thanh, Phạm Thị Năm, Trần Đại Lâm, Huỳnh Lê Thanh Nguyên, Nguyễn Thái ', './public/upload/2023-07-20-13-13-574. Thơm.pdf'),
(177, 'Tạp chí NCKH số 35', 'Sản xuất vật liệu từ phế phụ phẩm của ngành chế biến nông sản thay thế cho nhựa tổng \r\nhợp đang trở thành xu hướng gần đây. ', 'Sản xuất vật liệu từ phế phụ phẩm của ngành chế biến nông sản thay thế cho nhựa tổng \r\nhợp đang trở thành xu hướng gần đây. Trong nghiên cứu này, bã nghệ - phụ phẩm của quá \r\ntrình chế biến các sản phẩm từ củ nghệ được sử dụng làm nguyên liệu chính. Với mục tiêu \r\nlà xác định được điều kiện xử lý bã nghệ thích hợp để tạo màng bao gói thực phẩm, các \r\nthông số của quá trình xử lý cơ học và xử lý hóa học bã nghệ được khảo sát. Kết quả cho \r\nthấy, xử lý cơ học bã nghệ bằng máy nghiền bi ở 4 h - 600 vòng/phút, sau đó, xử lý hóa học \r\nbã nghệ bằng NaOH 2,5% trong 4 h và tẩy trắng bằng NaClO 3,3% ở 25ºC trong 4 h sẽ thu \r\nđược bã nghệ khi sử dụng để tạo màng có các đặc điểm về độ dày 71 µm, độ bền kéo 18,85 \r\nMPa, độ giãn dài khi đứt 2,54%, độ ẩm 14,6% và độ hòa tan 36,43%', 0, 1, 15, '', 'disable', 1, 1, '2022-10-04 00:00:00', '2023-07-20 18:15:31', NULL, 'Phạm Thị Thu Hoài1, Đỗ Thị Thạch Thảo, Phạm Thị Thu, Đặng Thảo Yến Linh', './public/upload/2023-07-20-13-15-314. C Hoài MI.pdf'),
(178, 'Tạp chí NCKH số 36', 'Mạng cảm biến vô tuyến công nghiệp cần có các giải pháp mã kênh mạnh với các gói tin \r\nngắn. ', 'Mạng cảm biến vô tuyến công nghiệp cần có các giải pháp mã kênh mạnh với các gói tin \r\nngắn. Mã tích là họ mã kênh chỉ cần với chiều dài từ mã ngắn, đã đạt khả năng sửa lỗi rất tốt \r\ndo có khoảng cách Hamming cực tiểu lớn. Vì quá trình giải mã phức tạp nên hiện nay mã \r\ntích vẫn chưa được đề xuất ứng dụng trong các hệ thống truyền tin. Với mong muốn khai \r\nthác khả năng kiểm soát lỗi của mã tích trong các ứng dụng truyền tin hiện đại, bài báo này \r\ndựa vào cơ sở lý thuyết giải mã tích, lựa chọn mã Hamming làm mã thành phần, đề xuất \r\nthuật toán giải mã lặp áp dụng phù hợp với mã tích cho chất lượng giải mã tốt với độ phức \r\ntạp chấp nhận được. Kết quả mô phỏng cho thấy, tại tỉ lệ lỗi bit (BER) 105\r\n, yêu cầu về tỉ lệ \r\ncông suất tín hiệu trên tạp âm chỉ cần 3,7 dB nếu sử dụng mã Hamming (31, 26) làm mã \r\nthành phần.\r\n', 0, 1, 15, '', 'disable', 1, 1, '2022-05-23 00:00:00', '2023-07-20 18:17:02', NULL, 'Nguyễn Thị Hồng Nhung, Phạm Văn Nam', './public/upload/2023-07-20-13-17-026.  Nhung MI.pdf'),
(179, 'Tạp chí NCKH số 37', 'Bài báo trình bày cách sử dụng Python lõi xây dựng một modul thực hiện tính toán một số \r\nphép tính toán ma trận. ', 'Bài báo trình bày cách sử dụng Python lõi xây dựng một modul thực hiện tính toán một số \r\nphép tính toán ma trận. Modul được xây dựng một cách trực quan, rõ ràng bởi tập các hàm \r\nđược lập trình dựa trên các thuật toán về các phép toán về ma trận trong đại số tuyến tính', 0, 1, 15, '', 'disable', 1, 1, '2022-09-12 00:00:00', '2023-07-20 18:18:53', NULL, 'Chu Bình Minh, Trần Bảo Trung, Nguyễn Mai Quyên', './public/upload/2023-07-20-13-18-536. Minh MI_10_4_2023.pdf'),
(180, 'Tạp chí NCKH số 38', 'In recent years, clean water is increasingly scarce due to increased human demand, \r\nuntreated wastewater is the cause of water quality deterioration.', 'In recent years, clean water is increasingly scarce due to increased human demand, \r\nuntreated wastewater is the cause of water quality deterioration. The aquaculture industry \r\nbrings benefits in terms of food for humans, but the water sourses can be easily polluted by \r\ndecomposition of redundant aqua feed and organic waste. In this study, saline wastewater \r\nis treated by electrochemical oxidation with two types of Graphite and Ti/RuO2 electrodes. \r\nExperiments were conducted to investigate the effect of reaction conditions such as salt \r\nconcentration, current density, electrolysis time and pH on COD removal efficiency and \r\nelectrode activity. Ti/RuO2 electrode showed better handling ability than graphite electrode \r\nat the same reaction condition. For the optimal efficiency of COD removal and anode \r\nactivity, the electrolysis of saline wastewater is set at 2% salt content, at 45 mA/cm2 current \r\ndensity, in 60 minutes reaction , at 6 pH value. In this condition, COD treatment efficiency \r\nreached 87.7% (anode graphite) and 92.9% (anode Ti/RuO2), electrode efficiency reached \r\n0,208 kg/h.A.m2\r\n(anode graphite) and 0,22 kg/h.A.m2\r\n(anode Ti/RuO2).\r\n', 0, 1, 15, '', 'disable', 1, 1, '1970-01-01 01:00:00', '2023-07-20 18:20:48', '2023-07-20 18:21:13', 'Le Thi My Hanh, Vu Xuan Minh, Pham Thi Lan, Nguyen Tuan Dung, Nguyen Thi Phuong Lan, Tran Dai Lam', './public/upload/2023-07-20-13-20-484. Hạnh.pdf'),
(181, 'Tạp chí NCKH số 11', 'Hoạt động kinh doanh của các doanh nghiệp nói chung và doanh nghiệp may mặc nói \r\nriêng ngày càng mở rộng và cạnh tranh giữa các doanh nghiệp càng trở nên gay gắt. Khi \r\nđó, thông tin trở thành một nguồn lực quan trọng đối với các doanh nghiệp nói riêng và \r\nđối với nền kinh tế xã hội nói chung. Ngày nay, thông tin kế toán được sử dụng nhằm \r\ncung cấp thông tin phục vụ công tác quản trị cho đơn vị, nhằm nâng cao hiệu quả hoạt \r\nđộng kinh doanh. Tổ chức công tác kế toán đóng vai trò quan trọng đối với công tác \r\nquản lý và quá trình ra quyết định kinh doanh với mục đích chính là để tạo ra thông tin \r\nphục vụ cho việc ra quyết định kinh doanh. Cách thu thập, phân tích, xử lý, phân phối và \r\nlưu trữ thông tin kế toán đã được thay đổi trong những năm qua; Tuy nhiên, thông tin kế\r\ntoán luôn luôn là một phần quan trọng trong hoạt động kinh doanh. Bên cạnh những mặt \r\nđã đạt được trong tổ chức công tác kế toán của các doanh nghiệp may mặc thì công tác \r\nnày cũng đang cho thấy còn nhiều hạn chế từ khâu thu thập, xử lý, cung cấp và phân \r\ntích thông tin', 'Hoạt động kinh doanh của các doanh nghiệp nói chung và doanh nghiệp may mặc nói \r\nriêng ngày càng mở rộng và cạnh tranh giữa các doanh nghiệp càng trở nên gay gắt. Khi \r\nđó, thông tin trở thành một nguồn lực quan trọng đối với các doanh nghiệp nói riêng và \r\nđối với nền kinh tế xã hội nói chung. Ngày nay, thông tin kế toán được sử dụng nhằm \r\ncung cấp thông tin phục vụ công tác quản trị cho đơn vị, nhằm nâng cao hiệu quả hoạt \r\nđộng kinh doanh. Tổ chức công tác kế toán đóng vai trò quan trọng đối với công tác \r\nquản lý và quá trình ra quyết định kinh doanh với mục đích chính là để tạo ra thông tin \r\nphục vụ cho việc ra quyết định kinh doanh. Cách thu thập, phân tích, xử lý, phân phối và \r\nlưu trữ thông tin kế toán đã được thay đổi trong những năm qua; Tuy nhiên, thông tin kế\r\ntoán luôn luôn là một phần quan trọng trong hoạt động kinh doanh. Bên cạnh những mặt \r\nđã đạt được trong tổ chức công tác kế toán của các doanh nghiệp may mặc thì công tác \r\nnày cũng đang cho thấy còn nhiều hạn chế từ khâu thu thập, xử lý, cung cấp và phân \r\ntích thông tin', 0, 1, 22, '', 'disable', 1, 1, '2016-09-25 00:00:00', '2023-07-20 20:04:03', NULL, 'Ngô Thành Nam', './public/upload/2023-07-20-15-04-0311. Nam.pdf'),
(182, 'Tạp chí NCKH số 12', 'Ngày nay, sự phát triển của công nghệ thông tin và mạng Internet đã dẫn đến sự thay đổi của các hoạt động marketing theo hướng gia tăng các ứng dụng trên nền tảng Internet. Việc tiếp cận và thỏa mãn nhu cầu khách hàng qua các kênh trực tuyến như email, mạng xã hội, website… đang ngày càng trở nên phổ biến. Trong những năm gần đây, các ngân hàng thương mại của Việt Nam đã tăng cường ứng dụng các công cụ marketing trực tuyến vào hoạt động của mình, giúp ngân hàng và khách hàng tiết kiệm được thời gian và chi phí giao dịch.\r\nTrên cơ sở hệ thống hóa một số vấn đề lý luận về các công cụ marketing trực tuyến và thực tiễn triển khai các công cụ này tại các ngân hàng thương mại Việt Nam thời gian qua, bài viết đã đưa ra các giải pháp nhằm tăng cường ứng dụng các công cụ marketing trực tuyến trong hoạt động giao dịch cũng như quảng bá hình ảnh của ngân hàng thương mại Việt Nam.\r\n', 'Ngày nay, sự phát triển của công nghệ thông tin và mạng Internet đã dẫn đến sự thay đổi của các hoạt động marketing theo hướng gia tăng các ứng dụng trên nền tảng Internet. Việc tiếp cận và thỏa mãn nhu cầu khách hàng qua các kênh trực tuyến như email, mạng xã hội, website… đang ngày càng trở nên phổ biến. Trong những năm gần đây, các ngân hàng thương mại của Việt Nam đã tăng cường ứng dụng các công cụ marketing trực tuyến vào hoạt động của mình, giúp ngân hàng và khách hàng tiết kiệm được thời gian và chi phí giao dịch.\r\nTrên cơ sở hệ thống hóa một số vấn đề lý luận về các công cụ marketing trực tuyến và thực tiễn triển khai các công cụ này tại các ngân hàng thương mại Việt Nam thời gian qua, bài viết đã đưa ra các giải pháp nhằm tăng cường ứng dụng các công cụ marketing trực tuyến trong hoạt động giao dịch cũng như quảng bá hình ảnh của ngân hàng thương mại Việt Nam.\r\n', 0, 1, 23, '', 'disable', 1, 1, '2017-02-20 00:00:00', '2023-07-20 20:14:19', NULL, 'Nguyễn Thu Quỳnh', './public/upload/2023-07-20-15-14-1912. Quỳnh (2).doc'),
(183, 'Tạp chí NCKH số 13', 'Thành tựu của hoạt động bán lẻ là kết quả của một chuỗi các hoạt động, phản ánh nỗ lực của các nhà bán lẻ và mức độ đóng góp vào sự tăng trưởng của nền kinh tế.  Mô hình nghiên cứu AERO đưa ra một cách tiếp cận nhằm nghiên cứu và đánh giá đầy đủ những tác động đến thành công của các nhà bán lẻ, chỉ ra những con đường để cải thiện hoạt động, nâng cao hiệu quả kinh doanh trong dịch vụ bán lẻ.  Bài viết cũng tiếp tục phát triển cách tiếp cận này trong điều kiện thị trường bán lẻ Việt Nam, nhằm gợi ý một số vấn đề cần thiết để nâng cao hiệu quả bán lẻ liên quan đến: nhân viên bán hàng, khách hàng và các hoạt động logicstics tại cửa hàng và việc ứng dụng công nghệ trong bán lẻ.', 'Thành tựu của hoạt động bán lẻ là kết quả của một chuỗi các hoạt động, phản ánh nỗ lực của các nhà bán lẻ và mức độ đóng góp vào sự tăng trưởng của nền kinh tế.  Mô hình nghiên cứu AERO đưa ra một cách tiếp cận nhằm nghiên cứu và đánh giá đầy đủ những tác động đến thành công của các nhà bán lẻ, chỉ ra những con đường để cải thiện hoạt động, nâng cao hiệu quả kinh doanh trong dịch vụ bán lẻ.  Bài viết cũng tiếp tục phát triển cách tiếp cận này trong điều kiện thị trường bán lẻ Việt Nam, nhằm gợi ý một số vấn đề cần thiết để nâng cao hiệu quả bán lẻ liên quan đến: nhân viên bán hàng, khách hàng và các hoạt động logicstics tại cửa hàng và việc ứng dụng công nghệ trong bán lẻ.', 0, 1, 26, '', 'disable', 1, 1, '2017-05-25 00:00:00', '2023-07-20 20:17:42', NULL, 'Phạm Thúy Hồng', './public/upload/2023-07-20-15-17-4212.13. PT Hồng.doc'),
(184, 'Tạp chí NCKH số 14', 'Hoạt động marketing của mỗi doanh nghiệp đóng một vai trò quan trọng, quyết định vị trí \r\ncủa doanh nghiệp trên thị trường. Nó bắt đầu từ việc nghiên cứu thị trường và sau đó \r\nquyết định các hàng hoá sẽ được sản xuất, thực hiện sản xuất và phân phối. Trước sức \r\nép hội nhập quốc tế, các doanh nghiệp vừa và nhỏ đang đứng trước thách thức và cơ \r\nhội, đòi hỏi phải đáp ứng yêu cầu ngày càng gay gắt của cơ chế kinh tế thị trường. \r\nNgười tiêu dùng ngày càng có nhiều sự lựa chọn hơn cho nhu cầu của mình. Vì vậy, để\r\ncác doanh nghiệp nâng cao được năng lực cạnh tranh trong thời kỳ hội nhập, nhất là các \r\ndoanh nghiệp vừa và nhỏ, cần phải tăng cường năng lực quản trị kinh doanh, trong đó \r\nđặc biệt chú trọng tới việc xây dựng một đội ngũ marketing năng động, hiệu quả. Bài báo \r\nphản ánh thực trạng tình hình nhân sự có chuyên môn marketing và đề xuất các giải \r\npháp, nhằm nâng cao chất lượng đội ngũ marketing. Những giải pháp này sẽ là cơ sở\r\nkhoa học cho việc áp dụng vào thực tế tại các doanh nghiệp vừa và nhỏ ở Việt Nam \r\nhiện nay.\r\n', 'Hoạt động marketing của mỗi doanh nghiệp đóng một vai trò quan trọng, quyết định vị trí \r\ncủa doanh nghiệp trên thị trường. Nó bắt đầu từ việc nghiên cứu thị trường và sau đó \r\nquyết định các hàng hoá sẽ được sản xuất, thực hiện sản xuất và phân phối. Trước sức \r\nép hội nhập quốc tế, các doanh nghiệp vừa và nhỏ đang đứng trước thách thức và cơ \r\nhội, đòi hỏi phải đáp ứng yêu cầu ngày càng gay gắt của cơ chế kinh tế thị trường. \r\nNgười tiêu dùng ngày càng có nhiều sự lựa chọn hơn cho nhu cầu của mình. Vì vậy, để\r\ncác doanh nghiệp nâng cao được năng lực cạnh tranh trong thời kỳ hội nhập, nhất là các \r\ndoanh nghiệp vừa và nhỏ, cần phải tăng cường năng lực quản trị kinh doanh, trong đó \r\nđặc biệt chú trọng tới việc xây dựng một đội ngũ marketing năng động, hiệu quả. Bài báo \r\nphản ánh thực trạng tình hình nhân sự có chuyên môn marketing và đề xuất các giải \r\npháp, nhằm nâng cao chất lượng đội ngũ marketing. Những giải pháp này sẽ là cơ sở\r\nkhoa học cho việc áp dụng vào thực tế tại các doanh nghiệp vừa và nhỏ ở Việt Nam \r\nhiện nay.\r\n', 0, 1, 27, '', 'disable', 1, 1, '2017-04-17 00:00:00', '2023-07-20 20:18:49', NULL, 'Đoàn Thị Diệp Uyển\r\n', './public/upload/2023-07-20-15-18-4911. uyển.pdf'),
(185, 'Tạp chí NCKH số 15', 'Là một quốc gia có nền kinh tế đang phát triển, Việt Nam đã và đang từng bước tiến vào \r\nhội nhập kinh tế toàn cầu. Để có thể cạnh tranh được trên thị trường đòi hỏi các doanh \r\nnghiệp phải có được phương án cải tiến về quản trị doanh nghiệp. Trong đó, việc xây \r\ndựng hệ thống thông tin kế toán quản trị đáp ứng nhu cầu thông tin kế toán cho các nhà \r\nquản lý là vấn đề then chốt. Từ đó, giúp các nhà quản lý đưa ra quyết định nhanh chóng, \r\nchính xác, góp phần nâng cao hiệu quả quản trị doanh nghiệp và hoạt động sản xuất\r\nkinh doanh.', 'Là một quốc gia có nền kinh tế đang phát triển, Việt Nam đã và đang từng bước tiến vào \r\nhội nhập kinh tế toàn cầu. Để có thể cạnh tranh được trên thị trường đòi hỏi các doanh \r\nnghiệp phải có được phương án cải tiến về quản trị doanh nghiệp. Trong đó, việc xây \r\ndựng hệ thống thông tin kế toán quản trị đáp ứng nhu cầu thông tin kế toán cho các nhà \r\nquản lý là vấn đề then chốt. Từ đó, giúp các nhà quản lý đưa ra quyết định nhanh chóng, \r\nchính xác, góp phần nâng cao hiệu quả quản trị doanh nghiệp và hoạt động sản xuất\r\nkinh doanh.', 0, 1, 23, '', 'disable', 1, 1, '2018-04-03 00:00:00', '2023-07-20 20:19:51', NULL, 'Trần Thị Quỳnh Giang', './public/upload/2023-07-20-15-19-5110. Giang.pdf'),
(186, 'Tạp chí NCKH số 16', 'Việc xác định chế độ cắt tối ưu cho quá trình mài nhằm nâng cao chất lượng chi tiết và \r\nhạ giá thành sản phẩm rất quan trọng trong quá trình gia công. Thép X12M thuộc nhóm \r\nthép hợp kim cao, có độ bền nhiệt và độ bền cơ học cao. Vật liệu này thường dùng để\r\nchế tạo các chi tiết máy chính xác, chịu tải va đập lớn, chịu mài mòn và ăn mòn hóa học.\r\nTrong bài báo này, trên cơ sở ứng dụng phần mềm toán học Mable, tác giả đã kết hợp \r\ngiữa phân tích lý thuyết với thực nghiệm để tìm ra bộ thông số chế độ cắt tối ưu khi gia \r\ncông thép X12M trên máy mài tròn ngoài.', 'Việc xác định chế độ cắt tối ưu cho quá trình mài nhằm nâng cao chất lượng chi tiết và \r\nhạ giá thành sản phẩm rất quan trọng trong quá trình gia công. Thép X12M thuộc nhóm \r\nthép hợp kim cao, có độ bền nhiệt và độ bền cơ học cao. Vật liệu này thường dùng để\r\nchế tạo các chi tiết máy chính xác, chịu tải va đập lớn, chịu mài mòn và ăn mòn hóa học.\r\nTrong bài báo này, trên cơ sở ứng dụng phần mềm toán học Mable, tác giả đã kết hợp \r\ngiữa phân tích lý thuyết với thực nghiệm để tìm ra bộ thông số chế độ cắt tối ưu khi gia \r\ncông thép X12M trên máy mài tròn ngoài.', 0, 1, 15, '', 'disable', 1, 1, '2018-05-05 00:00:00', '2023-07-20 20:21:41', NULL, 'Nguyễn Văn Trúc, Bùi Ánh Hưng, Trịnh Thị Mai', './public/upload/2023-07-20-15-21-41Tạp chí số 16.pdf'),
(187, 'Tạp chí NCKH số 17', 'Rung động là trạng thái giới hạn của tự dao động trong quá trình gia công, là nguyên nhân chính hạn chế năng suất và chất lượng chi tiết gia công. Vì vậy các công trình nghiên cứu hiện nay đều tập trung vào phát hiện, nhận dạng, tránh, ngăn ngừa, giảm hoặc loại trừ rung động. Có nhiều phương pháp phát hiện và đo rung động đã được đề xuất, mỗi phương pháp có ưu, nhược điểm riêng. Bài báo này trình bày phương pháp dựa trên quan hệ giữa lực mài và các thông số rung động. Kết quả nghiên cứu đã chỉ ra rằng tín hiệu lực cắt không chỉ giúp phát hiện rung động mà còn dự báo nó trước khi xảy ra.', 'Rung động là trạng thái giới hạn của tự dao động trong quá trình gia công, là nguyên nhân chính hạn chế năng suất và chất lượng chi tiết gia công. Vì vậy các công trình nghiên cứu hiện nay đều tập trung vào phát hiện, nhận dạng, tránh, ngăn ngừa, giảm hoặc loại trừ rung động. Có nhiều phương pháp phát hiện và đo rung động đã được đề xuất, mỗi phương pháp có ưu, nhược điểm riêng. Bài báo này trình bày phương pháp dựa trên quan hệ giữa lực mài và các thông số rung động. Kết quả nghiên cứu đã chỉ ra rằng tín hiệu lực cắt không chỉ giúp phát hiện rung động mà còn dự báo nó trước khi xảy ra.', 0, 1, 15, '', 'disable', 1, 1, '2018-05-26 00:00:00', '2023-07-20 20:23:10', NULL, 'Phạm Vũ Dũng', './public/upload/2023-07-20-15-23-10File 17 MI.doc'),
(188, 'Tạp chí NCKH số 19', 'Phát âm là phần không thể thiếu của việc học ngôn ngữ bởi vì không có nó chúng ta sẽ gặp rất nhiều khó khăn trong việc giao tiếp với nhau. Đặc biệt, người học là người Việt Nam chưa hiểu sâu rộng về cách phát âm tiếng Anh, thậm chí cả những người mong muốn trở thành giáo viên hay người phiên dịch tiếng Anh tương lai. Vấn đề này xuất phát từ thực tế là người học thiếu sự phản hồi thường xuyên về phát âm từ các giáo viên tiếng Anh của họ từ các khía cạch khác nhau của phát âm. Vì vậy nghiên cứu hành động này đã được thực hiện nhằm tìm ra các câu trả lời cho ba câu hỏi nghiên cứu: (1) Lí do dẫn đến việc sinh viên phát âm kém là gì? (2) Các vấn đề mà sinh viên thường gặp khi phát âm các ký hiệu phụ âm tiếng Anh là gì? (3) Phản hồi thường xuyên có thể nâng cao khả năng phát âm cho sinh viên không? Nếu có thì trong phạm vi nào?', 'Phát âm là phần không thể thiếu của việc học ngôn ngữ bởi vì không có nó chúng ta sẽ gặp rất nhiều khó khăn trong việc giao tiếp với nhau. Đặc biệt, người học là người Việt Nam chưa hiểu sâu rộng về cách phát âm tiếng Anh, thậm chí cả những người mong muốn trở thành giáo viên hay người phiên dịch tiếng Anh tương lai. Vấn đề này xuất phát từ thực tế là người học thiếu sự phản hồi thường xuyên về phát âm từ các giáo viên tiếng Anh của họ từ các khía cạch khác nhau của phát âm. Vì vậy nghiên cứu hành động này đã được thực hiện nhằm tìm ra các câu trả lời cho ba câu hỏi nghiên cứu: (1) Lí do dẫn đến việc sinh viên phát âm kém là gì? (2) Các vấn đề mà sinh viên thường gặp khi phát âm các ký hiệu phụ âm tiếng Anh là gì? (3) Phản hồi thường xuyên có thể nâng cao khả năng phát âm cho sinh viên không? Nếu có thì trong phạm vi nào?', 0, 1, 23, '', 'disable', 1, 1, '2019-05-15 00:00:00', '2023-07-20 20:25:27', NULL, 'Trần Thị Quỳnh', './public/upload/2023-07-20-15-25-2711. Quỳnh.doc'),
(189, 'Tạp chí NCKH số 20', 'Mục tiêu của bài viết là xác định các yếu tố ảnh hưởng tới giá phí kiểm toán trong các doanh nghiệp dệt may Việt Nam. Nghiên cứu được thực hiện trên 186 kiểm toán viên, sử dụng phương pháp phân tích PLS trên Smart PLS 3.0. Kết quả cho thấy, giá phí kiểm toán trong các doanh nghiệp dệt may Việt Nam được xác định bởi quy mô của công ty được kiểm toán, loại dịch vụ chuyên nghiệp do công ty kiểm toán cung cấp, sự an toàn của hệ thống kiểm soát nội bộ của công ty kiểm toán và liên kết của công ty kiểm toán với bốn công ty kiểm toán quốc tế lớn. Các yếu tố như vị trí của công ty kiểm toán và vị trí của công ty được kiểm toán, mức độ phức tạp của công ty được kiểm toán và loại báo cáo cần phải nộp của công ty kiểm toán là những yếu tố ít quan trọng nhất.', 'Mục tiêu của bài viết là xác định các yếu tố ảnh hưởng tới giá phí kiểm toán trong các doanh nghiệp dệt may Việt Nam. Nghiên cứu được thực hiện trên 186 kiểm toán viên, sử dụng phương pháp phân tích PLS trên Smart PLS 3.0. Kết quả cho thấy, giá phí kiểm toán trong các doanh nghiệp dệt may Việt Nam được xác định bởi quy mô của công ty được kiểm toán, loại dịch vụ chuyên nghiệp do công ty kiểm toán cung cấp, sự an toàn của hệ thống kiểm soát nội bộ của công ty kiểm toán và liên kết của công ty kiểm toán với bốn công ty kiểm toán quốc tế lớn. Các yếu tố như vị trí của công ty kiểm toán và vị trí của công ty được kiểm toán, mức độ phức tạp của công ty được kiểm toán và loại báo cáo cần phải nộp của công ty kiểm toán là những yếu tố ít quan trọng nhất.', 0, 1, 26, '', 'disable', 1, 1, '2019-05-28 00:00:00', '2023-07-20 20:26:44', NULL, 'Đinh Thị Kim Xuyến, Vũ Thị Vân Anh', './public/upload/2023-07-20-15-26-4411. Xuyến (đã sửa).doc'),
(190, 'Tạp chí NCKH số 22', 'Mô hình dạy học kết hợp - Blended Learning (B-Learning) là một hình thức dạy học đang \r\nđược nghiên cứu và triển khai rộng rãi trên thế giới. Những nghiên cứu cho thấy B-Learning \r\nkhá phù hợp với dạy học ở bậc đại học trong thời đại kỉ nguyên số. Bằng việc phân tích các \r\nmô hình B-Learning và các đặc điểm dạy học tại Trường Đại học Kinh tế - Kỹ thuật Công \r\nnghiệp (UNETI), tác giả đã đề xuất các định hướng thiết kế B-Learning phù hợp để nâng cao \r\nchất lượng dạy và học tại trường.\r\n', 'Mô hình dạy học kết hợp - Blended Learning (B-Learning) là một hình thức dạy học đang \r\nđược nghiên cứu và triển khai rộng rãi trên thế giới. Những nghiên cứu cho thấy B-Learning \r\nkhá phù hợp với dạy học ở bậc đại học trong thời đại kỉ nguyên số. Bằng việc phân tích các \r\nmô hình B-Learning và các đặc điểm dạy học tại Trường Đại học Kinh tế - Kỹ thuật Công \r\nnghiệp (UNETI), tác giả đã đề xuất các định hướng thiết kế B-Learning phù hợp để nâng cao \r\nchất lượng dạy và học tại trường.\r\n', 0, 1, 22, '', 'disable', 1, 1, '2020-05-26 00:00:00', '2023-07-20 20:28:03', NULL, 'Nguyễn Trường Giang\r\n', './public/upload/2023-07-20-15-28-0312. Giang.pdf'),
(191, 'Tạp chí NCKH số 23', 'Mục tiêu của bài báo là tổng quan các nghiên cứu liên quan tới khởi nghiệp, ý định khởi nghiệp, các doanh nghiệp khởi nghiệp và quan trọng hơn cả là các chỉ tiêu, tiêu chí đánh giá hiệu quả hoạt động của các doanh nghiệp khởi nghiệp. Bài báo là nền tảng quan trọng để các nghiên cứu trong tương lai về chủ đề khởi nghiệp và đánh giá hiệu quả hoạt động của các doanh nghiệp khởi nghiệp kế thừa và phát triển', 'Mục tiêu của bài báo là tổng quan các nghiên cứu liên quan tới khởi nghiệp, ý định khởi nghiệp, các doanh nghiệp khởi nghiệp và quan trọng hơn cả là các chỉ tiêu, tiêu chí đánh giá hiệu quả hoạt động của các doanh nghiệp khởi nghiệp. Bài báo là nền tảng quan trọng để các nghiên cứu trong tương lai về chủ đề khởi nghiệp và đánh giá hiệu quả hoạt động của các doanh nghiệp khởi nghiệp kế thừa và phát triển', 0, 1, 24, '', 'disable', 1, 1, '2020-05-25 00:00:00', '2023-07-20 20:32:24', NULL, 'Trần Ngọc Ban', './public/upload/2023-07-20-15-32-2411. Ban.doc'),
(192, 'Tạp chí NCKH số 24', 'Nghiên cứu này được thực hiện để tìm hiểu nhận thức của các doanh nghiệp Việt Nam về những lợi ích khi áp dụng giá trị hợp lý (GTHL) trong kế toán và ảnh hưởng của nó đến sự ủng hộ áp dụng. Dữ liệu nghiên cứu được thu thập thông qua khảo sát quan điểm của người lập Báo cáo tài chính (BCTC) tại các doanh nghiệp Việt Nam. Kết quả nghiên cứu cho thấy rằng nhận thức về lợi ích nâng cao chất lượng BCTC và tăng tính hợp pháp quốc tế khi áp dụng là những nhân tố có ảnh hưởng tích cực đến sự ủng hộ áp dụng GTHL trong kế toán tại các doanh nghiệp Việt Nam.', 'Nghiên cứu này được thực hiện để tìm hiểu nhận thức của các doanh nghiệp Việt Nam về những lợi ích khi áp dụng giá trị hợp lý (GTHL) trong kế toán và ảnh hưởng của nó đến sự ủng hộ áp dụng. Dữ liệu nghiên cứu được thu thập thông qua khảo sát quan điểm của người lập Báo cáo tài chính (BCTC) tại các doanh nghiệp Việt Nam. Kết quả nghiên cứu cho thấy rằng nhận thức về lợi ích nâng cao chất lượng BCTC và tăng tính hợp pháp quốc tế khi áp dụng là những nhân tố có ảnh hưởng tích cực đến sự ủng hộ áp dụng GTHL trong kế toán tại các doanh nghiệp Việt Nam.', 0, 1, 23, '', 'disable', 1, 1, '2020-04-28 00:00:00', '2023-07-20 20:33:39', NULL, 'Nguyễn Ngọc Lan', './public/upload/2023-07-20-15-33-3912. NN Lan Mi.doc');
